INSERT INTO `services` (`id`, `en_service_title`, `ar_service_title`, `en_service_text`, `ar_service_text`, `en_slug`, `ar_slug`, `en_meta_title`, `ar_meta_title`, `en_meta_text`, `ar_meta_text`, `service_type`, `main_image`, `home_status`, `active_status`, `order_view`, `created_at`, `updated_at`) VALUES
(1, 'FINISHING WORKS', 'أعمال التشطيبات', '<p>Every project we finish requires superior performance and design. With over 20 years of combined experience,\r\nwe are uniquely qualified to deliver exceptional quality.\r\nWe are an elite applicator, meeting stringent quality performance standards. We have become one of the most reputable companies in the finishing works and a leader when it comes to quality and customer needs. And We provide integrated services for landscaping, both residential and commercial, located in the KSA. We offer a full range of services related to the design, construction and maintenance of gardens and green spaces.\r\nWe are committed to demonstrating the wonderful side of nature, and at the same time to being as an integral part\r\nof the preservation process.</p>', '<p>كل مشروع ننهيه يتطلب أداءً وتصميمًا ممتازين. مع أكثر من 20 عامًا من الخبرة المشتركة، نحن مؤهلون بشكل فريد لتقديم جودة استثنائية.\r\nنحن من التطبيقين المتميزين الذين يلتزمون بمعايير أداء الجودة الصارمة. لقد أصبحنا واحدة من أكثر الشركات سمعة في أعمال التشطيبات وقادة في مجال الجودة واحتياجات العملاء.\r\nنحن نقدم خدمات متكاملة للمناظر الطبيعية، سواء للمشاريع السكنية أو التجارية في المملكة العربية السعودية. نقدم مجموعة كاملة من الخدمات المتعلقة بتصميم وبناء وصيانة الحدائق والمساحات الخضراء.\r\nنحن ملتزمون بإظهار الجانب الرائع للطبيعة، وفي الوقت نفسه نحرص على أن نكون جزءًا لا يتجزأ من عملية الحفاظ عليها.</p>', NULL, NULL, 'FINISHING WORKS', 'أعمال التشطيبات', 'FINISHING WORKS', 'أعمال التشطيبات', 'service', '1744031167VWQHOYqHvi.webp', 1, 1, 4, '2025-02-06 20:00:19', '2025-04-07 18:06:07'),
(2, 'MEP Works', 'الأعمال الكهربائية والميكانيكية', '<p>ELECTRICAL AND MECHANICAL WORK\r\nWe understand the critical role that electrical and mechanical systems play in the functionality, safety, and effciency of any building.\r\nAs a trusted provider of electrical and mechanical works, we offer comprehensive services that ensure reliable power distribution, energy efficiency, and compliance with industry standards.\r\nAt FPICO, we approach electrical and mechanical works with\r\na focus on technical expertise, meticulous planning,\r\nand customer satisfaction.\r\nOur team of experienced workers and engineers brings extensive knowledge and a commitment to quality craftsmanship to every project.\r\nWe prioritize safety, efficiency, and innovative solutions to meet the\r\nunique electrical and mechanical needs of our clients.</p>', '<p>نحن ندرك الدور الحيوي الذي تلعبه الأنظمة الكهربائية والميكانيكية في وظيفة وأمان وكفاءة أي مبنى.\r\nبوصفنا مقدمين موثوقين للأعمال الكهربائية والميكانيكية، نقدم خدمات شاملة تضمن توزيعًا موثوقًا للطاقة، وكفاءة في استخدام الطاقة، والامتثال للمعايير الصناعية.\r\nفي FPICO، نتعامل مع الأعمال الكهربائية والميكانيكية مع التركيز على الخبرة التقنية، والتخطيط الدقيق، ورضا العملاء.\r\nيجلب فريقنا من العمال والمهندسين ذوي الخبرة معرفة واسعة واهتمامًا بالحرفية ذات الجودة العالية لكل مشروع.\r\nنحن نولي الأولوية للسلامة والكفاءة والحلول المبتكرة لتلبية الاحتياجات الكهربائية والميكانيكية الفريدة لعملائنا.</p>', NULL, NULL, 'ELECTRICAL AND MECHANICAL WORK', 'الأعمال الكهربائية والميكانيكية', 'ELECTRICAL AND MECHANICAL WORK', 'الأعمال الكهربائية والميكانيكية', 'service', '1744031045eZvlFOgxDz.webp', 1, 1, 5, '2025-02-06 20:02:11', '2025-04-07 18:04:05'),
